// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract DataIntegrityVerifier {
    // State variables
    address public owner;
    mapping(address => bytes32) public userDataHashes;

    // Events
    event DataStored(address user, bytes32 hash);
    event DataVerified(address user, bool isIntegrityValid);

    // Modifier for restricting access to the owner
    modifier onlyOwner() {
        require(msg.sender == owner, "Only the owner can call this function");
        _;
    }

    // Constructor to set the contract owner
    constructor() {
        owner = msg.sender;
    }

    // Function for users to store the hash of their data
    function storeUserData(bytes32 dataHash) public {
        userDataHashes[msg.sender] = dataHash;
        emit DataStored(msg.sender, dataHash);
    }

    // Function to verify the integrity of the stored data
    function verifyDataIntegrity(bytes memory userData) public returns (bool) {
        bytes32 currentHash = keccak256(userData);
        bool isIntegrityValid = currentHash == userDataHashes[msg.sender];
        emit DataVerified(msg.sender, isIntegrityValid);
        return isIntegrityValid;
    }

    // Function to change the contract owner (restricted to the current owner)
    function changeOwner(address newOwner) public onlyOwner {
        owner = newOwner;
    }
}
